mapper.delete:
	/*체크된 폐기컵 삭제*/
		DELETE FROM cup_discard_manage
		WHERE
			cup_QR_code IN
			
    	
      		#{item}

mapper.select:
/*폐기컵 조회*/ SELECT cdm.approval_partner_plus_info_code AS partnerInfo, cdm.cup_QR_code AS cupQR, cdm.admin_id AS adminId, cdm.up_datetime AS upDateTime FROM cup_discard_manage AS cdm
ORDER BY CAST(SUBSTRING_INDEX(cdm.cup_discard_manage_code,'code',-1) AS UNSIGNED)
LIMIT #{startIndex},#{rowPerPage};

mapper.delete:
	/*체크된 컵 삭제*/
		DELETE FROM cup_manage
		WHERE
			cup_QR_code IN
			
    	
      		#{item}

mapper.update:
/* 상품수정 */ UPDATE cup_manage AS cm
cm.static_code = #{staticCode},
cm.admin_id = #{userId},
cm.up_datetime =NOW()
WHERE cm.cup_QR_code =#{cupQR};

mapper.select:
/*컵 상태 조회*/ SELECT cm.cup_QR_code as cupQR, sc.static_code_content as staticCode, cm.set_datetime as setDate, cm.admin_id as adminId, cm.up_datetime as upDateTime FROM cup_manage AS cm INNER join static_code AS sc on cm.static_code = sc.static_code
${searchKey} = #{searchValue}
ORDER BY CAST(SUBSTRING(cm.cup_QR_code,6) AS UNSIGNED)
LIMIT #{startIndex},#{rowPerPage};
